INSERT INTO `tin_tuc` (`ma_tin_tuc`, `tieu_de`, `noi_dung_ngan`, `noi_dung`, `anh`, `ngay_dang_tin`, `tac_gia`) VALUES
(1, 'Tiger-Striped Nike React – Mẫu giầy “hổ báo” sẽ sớm được diện kiến ra mắt ', 'Những bóng hồng của dòng React – React Presto, mặc dù là một phần của của đại gia đình Swoosh như bao nhân vật khác, có thể thấy trong năm qua nó vẫn chưa thấy được đầu tư mở rộng như React Element 55, 87 hay Air Max 270...', '<p>Những b&oacute;ng hồng của d&ograve;ng React &ndash; React Presto, mặc d&ugrave; l&agrave; một phần của của đại gia đ&igrave;nh Swoosh như bao nh&acirc;n vật kh&aacute;c, c&oacute; thể thấy trong năm qua n&oacute; vẫn chưa thấy được đầu tư mở rộng như React Element 55, 87 hay Air Max 270. Tuy nhi&ecirc;n khởi đầu năm mới 2020, Nike c&oacute; vẻ đang rục rịch chuẩn bị đưa Nike React l&ecirc;n một tầm cao mới &ndash; một thiết kế dự đo&aacute;n sẽ g&acirc;y b&atilde;o trong cộng đồng sneaker &ndash; Tiger-Striped Nike React.</p>\r\n\r\n<p>Tiger-Striped Nike React được thiết kế đặc biệt với hoạ tiết động vật &ndash; đ&uacute;ng với hướng đi của Nike trong thời gian gần đ&acirc;y. Tuy nhi&ecirc;n kh&ocirc;ng giống như c&aacute;c mặt h&agrave;ng chủ lực l&agrave; Air Jordan 3, đ&ocirc;i sneaker n&agrave;y chỉ c&oacute; một điểm nhấn &ndash; đ&oacute; l&agrave; một bản in l&ocirc;ng hổ nổi bật với c&aacute;ch dệt vải tinh tế. Kết cấu cuả đ&ocirc;i React n&agrave;y được lựa chọn gần như ho&agrave;n hảo, m&agrave;u sắc ch&acirc;n thực thay v&igrave; đồ họa 2D &ldquo;ba xu&rdquo;.</p>\r\n\r\n<p>Nh&igrave;n chung th&igrave; tổng thể đ&ocirc;i gi&agrave;y được nhận đầy đủ c&aacute;c chi tiết của một con hổ đ&iacute;ch thực mặc d&ugrave; kh&ocirc;ng c&oacute; bất kỳ tấm da thực tế n&agrave;o. Chi tiết li&ecirc;n kết giữa upper &amp; midsole, d&acirc;y buộc v&agrave; g&oacute;t sau bằng miếng vải h&ograve;a quyện với c&aacute;c sọc th&ocirc;ng qua m&agrave;u đen.</p>\r\n\r\n<p>Đế midsole trắng tinh tươm gi&uacute;p đ&ocirc;i &ldquo;hổ b&aacute;o&rdquo; n&agrave;y trở n&ecirc;n dung ho&agrave;. H&igrave;nh ảnh ở đế sau được khắc hoạ như những đốm lửa cam &amp; đinh t&aacute;n, phối c&ugrave;ng m&agrave;u đen khiến n&oacute; trở l&ecirc;n bốc lửa hơn bao giờ hết.</p>\r\n\r\n<p><em>H&igrave;nh ảnh chi tiết:</em></p>\r\n\r\n<p><img alt=\"Tiger-Striped Nike React \" src=\"https://i0.wp.com/snkrvn.com/wp-content/uploads/2019/12/Tiger-Striped-Nike-React-1.jpg?resize=740%2C519&amp;ssl=1\" /> <img alt=\"Tiger-Striped Nike React \" src=\"https://i2.wp.com/snkrvn.com/wp-content/uploads/2019/12/Tiger-Striped-Nike-React-2.jpg?resize=740%2C519&amp;ssl=1\" /> <img alt=\"Tiger-Striped Nike React \" src=\"https://i1.wp.com/snkrvn.com/wp-content/uploads/2019/12/Tiger-Striped-Nike-React-3.jpg?resize=740%2C740&amp;ssl=1\" /> <img alt=\"Tiger-Striped Nike React \" src=\"https://i0.wp.com/snkrvn.com/wp-content/uploads/2019/12/Tiger-Striped-Nike-React-4.jpg?resize=740%2C519&amp;ssl=1\" /> <img alt=\"Tiger-Striped Nike React \" src=\"https://i0.wp.com/snkrvn.com/wp-content/uploads/2019/12/Tiger-Striped-Nike-React-5.jpg?resize=740%2C519&amp;ssl=1\" /></p>\r\n', 'Tiger-Striped-Nike-React.jpg', '2020-05-04', 'Kim Jong-hyun'),
(2, 'Tiết lộ Adidas ra mắt giày Nemeziz 19+ thế hệ mới', 'Một thế hệ linh động mới đã xuất hiện, khi Adidas cho ra mắt Nemeziz 19 trước phần còn lại của dòng “302 Redirect”. Chiếc giày mới được phát triển đi kèm với một loạt các cải tiến nâng cao, mang đến sự nhanh nhẹn và hiệu suất 360...', '<p>Một thế hệ linh động mới đ&atilde; xuất hiện, khi Adidas cho ra mắt Nemeziz 19 trước phần c&ograve;n lại của d&ograve;ng &ldquo;302 Redirect&rdquo;. Chiếc gi&agrave;y mới được ph&aacute;t triển đi k&egrave;m với một loạt c&aacute;c cải tiến n&acirc;ng cao, mang đến sự nhanh nhẹn v&agrave; hiệu suất 360 cho những cầu thủ như Roberto Firmino, Bernardo Silva, Moise Kean v&agrave; dĩ nhi&ecirc;n l&agrave; Lionel Messi.</p>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/100/347/092/files/adidas-ra-mat-giay-nemeziz-19-the-he-moi.jpg?v=1563692544955\" /></p>\r\n\r\n<p>N&oacute; đ&atilde; được n&acirc;ng cấp trong một thời gian, v&agrave; giờ Nemeziz 19 mới cuối c&ugrave;ng đ&atilde; xuất hiện. C&oacute; thể n&oacute;i, Adidas tr&igrave;nh b&agrave;y một trang bị lại cải thiện đ&aacute;ng kể tr&ecirc;n người tiền nhiệm của n&oacute;.</p>\r\n\r\n<p>Đ&ocirc;i khi c&aacute;c bản n&acirc;ng cấp kh&ocirc;ng thực sự l&agrave;m thay đổi t&iacute;nh thẩm mỹ hay thậm ch&iacute; hiệu suất của một mẫu gi&agrave;y, nhưng điều đ&oacute; chắc chắn kh&ocirc;ng xảy ra với Nemeziz 19+ bởi n&oacute; gi&uacute;p cải thiện cấu tr&uacute;c phần tr&ecirc;n mang t&iacute;nh c&aacute;ch mạng cũng như giới thiệu một mặt đế mới.</p>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/100/347/092/files/1.jpg?v=1563692556270\" /></p>\r\n\r\n<p>Ra mắt với một bản m&agrave;u đỏ ch&oacute;i lọi với đế ngo&agrave;i m&agrave;u bạc lấp l&aacute;nh, Nemeziz 19+ c&oacute; cấu tr&uacute;c v&agrave; th&agrave;nh phần dệt mới, mang lại sự vừa vặn chưa từng thấy cho ch&acirc;n.</p>\r\n\r\n<p>Băng th&iacute;ch ứng tối đa h&oacute;a sự linh động v&agrave; tăng tốc cho một thay đổi hướng nhanh hơn, trong khi cổ băng h&igrave;nh chữ V mới cho ph&eacute;p xỏ gi&agrave;y v&agrave;o dễ d&agrave;ng v&agrave; &ocirc;m chặt mắt c&aacute; ch&acirc;n m&agrave; kh&ocirc;ng giới hạn chuyển động. N&oacute; cũng mang lại cho Nemeziz 19+ một h&igrave;nh b&oacute;ng hoang d&atilde; kh&ocirc;ng giống với bất cứ thứ g&igrave; bạn đ&atilde; thấy trước đ&acirc;y, dựa tr&ecirc;n di sản m&agrave; Nemeziz đ&atilde; tạo ra cho đến nay.</p>\r\n\r\n<p><img src=\"https://bizweb.dktcdn.net/100/347/092/files/2.jpg?v=1563692613034\" /></p>\r\n\r\n<p>Phải thừa nhận Nemeziz 19+ đ&atilde; g&acirc;y ra sự t&ograve; m&ograve; nhất định bởi đ&ocirc;i gi&agrave;y rất tuyệt vời v&agrave; hứa hẹn nhiều điều hấp dẫn. C&aacute;i nh&igrave;n ấn tượng đầu ti&ecirc;n l&agrave; ba sọc quen thuộc của Adidas nằm ở g&oacute;t ch&acirc;n, kết hợp rực rỡ với phần đến m&agrave;u bạc đứt g&atilde;y. R&otilde; r&agrave;ng, m&agrave;u bạc rất nổi bật, tương phản với m&agrave;u đỏ nền của đ&ocirc;i gi&agrave;y.</p>\r\n\r\n<p>Nemeziz 19 cũng sẽ c&oacute; sẵn với phi&ecirc;n bản 19.1 c&oacute; d&acirc;y buộc, t&ugrave;y thuộc v&agrave;o sở th&iacute;ch của bạn v&agrave; n&oacute; sẽ xuất hiện như một phần của trong d&ograve;ng &ldquo;302 Redirect&rdquo; của Adidas, b&ecirc;n cạnh Predator, COPA v&agrave; X thế hệ mới. Ch&uacute;ng ta hi vọng sẽ thấy tất cả tr&ecirc;n s&acirc;n cỏ trong phần c&ograve;n lại của m&ugrave;a giải 2018/19, trong đ&oacute; c&oacute; cả cả chung kết C&uacute;p C1/Champions League.</p>\r\n', 'Nemeziz-19.jpg', '2020-05-04', 'Kim Jong-hyun'),
(3, 'Ấn định ngày phát hành cho Nike Kyrie Low 2 “Black/Metallic', 'Với mùa giải tiếp theo thi đấu trong màu áo Đen/Trắng cùng Kevin Durant tại Brooklyn Nets, đây sẽ là bộ đôi được dự đoán trở thành ứng cử viên cho chức vô địch năm nay...', '<p>Với m&ugrave;a giải tiếp theo thi đấu trong m&agrave;u &aacute;o Đen/Trắng c&ugrave;ng Kevin Durant tại Brooklyn Nets, đ&acirc;y sẽ l&agrave; bộ đ&ocirc;i được dự đo&aacute;n trở th&agrave;nh ứng cử vi&ecirc;n cho chức v&ocirc; địch năm nay (d&ugrave; th&ocirc;ng tin về việc Kevin Durant c&oacute; khả năng bỏ lỡ m&ugrave;a giải n&agrave;y l&agrave; rất cao).</p>\r\n\r\n<p>Tạm g&aacute;c lại vấn đề n&oacute;i tr&ecirc;n, m&ugrave;a giải NBA 2019-2020 ng&agrave;y một gần kề v&agrave; đ&acirc;y cũng l&agrave; dịp để c&aacute;c thương hiệu tiếp tục giới thiệu đến những mẫu gi&agrave;y b&oacute;ng rổ mới. C&aacute;i t&ecirc;n Nike Kyrie Low đ&atilde; lộ diện thiết kế thứ 2 trong phối m&agrave;u Black/Metallic Silver c&ugrave;ng th&ocirc;ng tin ph&aacute;t h&agrave;nh v&agrave; gi&aacute; b&aacute;n lẻ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://hnbmg.com/wp-content/uploads/nike-kyrie-low-2-av6337-003-3.jpg\" /></p>\r\n\r\n<p>Được x&acirc;y dựng để ph&ugrave; hợp với lối chơi tốc độ v&agrave; mang t&iacute;nh đột ph&aacute; như Kyrie Irving, một thiết kế cổ thấp lu&ocirc;n được đề cao. Chất liệu mesh mỏng v&agrave; nhẹ được d&ugrave;ng cho phần upper với mục đ&iacute;ch cắt giảm c&acirc;n nặng hết mức c&oacute; thể. Hỗ trợ cho người mang trong phi&ecirc;n bản n&agrave;y l&agrave; 2 lớp nhựa cứng đặt ở 2 mặt b&ecirc;n tr&ecirc;n th&acirc;n gi&agrave;y, t&iacute;ch hợp k&egrave;m c&aacute;c mắt xỏ d&acirc;y v&agrave; logo Swoosh m&agrave;u bạc kim. Ở mặt trong, phần nhựa cứng được li&ecirc;n kết chắc chắn với bộ mid-sole. Yếu tố duy nhất được giữ lại ch&iacute;nh l&agrave; bộ đệm Zoom Air, mang đến sự thoải m&aacute;i v&agrave; độ phản hồi nhanh nhạy.</p>\r\n\r\n<p><img alt=\"\" src=\"https://hnbmg.com/wp-content/uploads/nike-kyrie-low-2-av6337-003-6.jpg\" /> <img alt=\"\" src=\"https://hnbmg.com/wp-content/uploads/nike-kyrie-low-2-av6337-003-4.jpg\" /> <img alt=\"\" src=\"https://hnbmg.com/wp-content/uploads/nike-kyrie-low-2-av6337-003-1.jpg\" /> <img alt=\"\" src=\"https://hnbmg.com/wp-content/uploads/nike-kyrie-low-2-av6337-003-2.jpg\" /> <img alt=\"\" src=\"https://hnbmg.com/wp-content/uploads/nike-kyrie-low-2-av6337-003-5.jpg\" /></p>\r\n\r\n<p>Theo th&ocirc;ng tin ph&aacute;t h&agrave;nh ch&iacute;nh thức, Nike Kyrie Low 2 &ldquo;Black/Metallic&rdquo; sẽ mở b&aacute;n v&agrave;o ng&agrave;y 01.10.2019 tại c&aacute;c cửa h&agrave;ng b&aacute;n lẻ của Nike Basketball.</p>\r\n', 'Nike-kyrie-low-2.jpg', '2020-05-04', 'Kim Jong-hyun'),
(4, 'Sắp ra mắt Adidas ZX 8000 “Lethal Nights” trên thị trường Việt', 'Sau khi hoàn thành diện mạo với phong cách retro dựa trên bản gốc “Aqua”, adidas ZX 8000 tiếp tục được chú ý khi tung ra tin tức rằng sẽ sớm mang đến một bóng hình rắn rỏi độc lạ...', '<p>Sau khi ho&agrave;n th&agrave;nh diện mạo với phong c&aacute;ch retro dựa tr&ecirc;n bản gốc &ldquo;Aqua&rdquo;, adidas ZX 8000 tiếp tục được ch&uacute; &yacute; khi tung ra tin tức rằng sẽ sớm mang đến một b&oacute;ng h&igrave;nh rắn rỏi độc lạ v&agrave; cũng l&agrave; một phần của bộ sưu tập &ldquo;Lethal Nights&rdquo; c&ugrave;ng t&ecirc;n ...</p>\r\n\r\n<p>Mặc d&ugrave; hầu hết c&aacute;c chi tiết về bộ sưu tập n&agrave;y vẫn chưa được biết, nhưng những người ủng hộ dự đo&aacute;n rằng diện mạo của c&aacute;c mẫu adidas cho retro runner n&agrave;y sẽ l&agrave; c&aacute;c phối m&agrave;u v&agrave; họa tiết lấy cảm hứng từ những sinh vật chết người c&oacute; thể gặp phải từ cuộc sống thường ng&agrave;y.</p>\r\n\r\n<p><img alt=\"adidas ZX 8000\" src=\"https://i2.wp.com/snkrvn.com/wp-content/uploads/2020/01/adidas-ZX-8000-“Lethal-Nights”-1.jpg?resize=740%2C520&amp;ssl=1\" /></p>\r\n\r\n<p>Lựa chọn một bảng m&agrave;u n&acirc;u v&agrave; kem, thiết kế da rắn ch&acirc;n thật c&ugrave;ng một chiếc đế trắng trơn tương phản, tổng thể đ&ocirc;i sneaker n&agrave;y tạo cảm gi&aacute;c d&ugrave; l&agrave;m từ chất liệu kh&aacute; sang chảnh nhưng vẫn mang n&eacute;t năng động trẻ trung. Ở những nơi kh&aacute;c tr&ecirc;n gi&agrave;y, adidas cũng tinh tế l&ograve;ng nhưng miếng da trắng tinh tươm để l&agrave;m nổi bật đ&ocirc;i gi&agrave;y, đặc biệt l&agrave; dấu ấn thương hiệu &ldquo;ba sọc&rdquo; huyền thoại.</p>\r\n\r\n<p>Để th&ecirc;m phần đặc sắc, &ldquo;Lethal Nights&rdquo; phi&ecirc;n bản đặc biệt n&agrave;y xuất hiện c&aacute;c chi tiết 3M phản quang ở đế midsole b&agrave;n ch&acirc;n trước v&agrave; g&oacute;t ch&acirc;n. Ngo&agrave;i tag in d&ograve;ng chữ adidas lu&ocirc;n được đặt ở phần lưỡi g&agrave; th&igrave; phi&ecirc;n bản n&agrave;y được nhấn nh&aacute; một lace tag xanh l&aacute; mang t&ecirc;n d&ograve;ng sneaker &ldquo;Torsion&rdquo;</p>\r\n\r\n<p><em>H&igrave;nh ảnh chi tiết của đ&ocirc;i adidas ZX 8000 &ldquo;Lethal Nights&rdquo;:</em></p>\r\n\r\n<p><img alt=\"adidas ZX 8000\" src=\"https://i2.wp.com/snkrvn.com/wp-content/uploads/2020/01/adidas-ZX-8000-“Lethal-Nights”.jpg?resize=740%2C520&amp;ssl=1\" /> <img alt=\"adidas ZX 8000\" src=\"https://i1.wp.com/snkrvn.com/wp-content/uploads/2020/01/adidas-ZX-8000-“Lethal-Nights”-2.jpg?resize=740%2C520&amp;ssl=1\" /> <img alt=\"adidas ZX 8000\" src=\"https://i1.wp.com/snkrvn.com/wp-content/uploads/2020/01/adidas-ZX-8000-“Lethal-Nights”-3.jpg?resize=740%2C520&amp;ssl=1\" /> <img alt=\"adidas ZX 8000\" src=\"https://i1.wp.com/snkrvn.com/wp-content/uploads/2020/01/adidas-ZX-8000-“Lethal-Nights”-4.jpg?resize=740%2C740&amp;ssl=1\" /> <img alt=\"adidas ZX 8000\" src=\"https://i1.wp.com/snkrvn.com/wp-content/uploads/2020/01/adidas-ZX-8000-“Lethal-Nights”-5.jpg?resize=740%2C740&amp;ssl=1\" /></p>\r\n\r\n<p>Mẫu sneaker da rắn n&agrave;y c&oacute; thể sẽ được đặt tr&ecirc;n kệ c&aacute;c cửa h&agrave;ng b&aacute;n lẻ của <a href=\"#\">adidas</a> v&agrave;o ng&agrave;y 06 th&aacute;ng 02 với gi&aacute; dao động trong khoảng &euro;139.95. Một sự kết hợp giữa hiện đại v&agrave; retro c&oacute; thể gi&uacute;p bạn đẩy style của m&igrave;nh l&ecirc;n một tầm cao &ldquo;sang chảnh&rdquo; mới l&agrave; đ&acirc;y.</p>\r\n', 'Adidas-ZX-8000.jpg', '2020-05-04', 'Kim Jong-hyun'),
(5, 'Sắp ra mắt Air Max 270 React trên thị trường Việt ', 'Mặc dù Nike và Travis Scott sẽ sớm mở ra một diện mạo mới cho Air Max 270 React, nhưng chắc chắn một điều rằng Swoosh sẽ không bỏ quên những phối màu đặc biệt...', '<p>Mặc d&ugrave; Nike v&agrave; Travis Scott sẽ sớm mở ra một diện mạo mới cho Air Max 270 React, nhưng chắc chắn một điều rằng Swoosh sẽ kh&ocirc;ng bỏ qu&ecirc;n những phối m&agrave;u đặc biệt d&agrave;nh cho ph&aacute;i đẹp thế giới trong năm 2020. V&agrave; tiếp nối phi&ecirc;n bản Travis Scott, Air Max 270 React chuẩn bị x&acirc;y dựng một h&igrave;nh ảnh nữ t&iacute;nh với phối m&agrave;u Mauve dự đo&aacute;n xuất hiện trong bộ sưu tập năm mới 2020 ở Beaverton.</p>\r\n\r\n<p><img alt=\"Air Max 270 React\" src=\"https://i1.wp.com/snkrvn.com/wp-content/uploads/2020/01/Air-Max-270-React.jpg?resize=740%2C520&amp;ssl=1\" /></p>\r\n\r\n<p>Ch&acirc;n dung đ&ocirc;i &ldquo;chunky&rdquo; tiếp tục xuất hiện với phối m&agrave;u ấn tượng được đ&aacute;nh gi&aacute; giống phi&ecirc;n bản &ldquo;Plum Chalk&rdquo;. Được xem l&agrave; phi&ecirc;n bản &ldquo;Year Of The Rat&rdquo;, đ&ocirc;i sneaker sắp ra mắt n&agrave;y chọn một bảng m&agrave;u nhẹ nh&agrave;ng tựa như một buổi chiều ho&agrave;ng h&ocirc;n Cali với c&aacute;c tone m&agrave;u t&iacute;m c&agrave;, x&aacute;m, hồng pastel v&agrave; m&agrave;u &ldquo;da r&aacute;m nắng&rdquo;. Bởi vậy m&agrave; hỗn hợp m&agrave;u sắc n&agrave;y chắc chắn sẽ l&agrave; sự lựa chọn ho&agrave;n hảo của c&aacute;c bạn g&aacute;i trong m&ugrave;a xu&acirc;n n&agrave;y.</p>\r\n\r\n<p>T&ocirc;ng m&agrave;u tối nhất l&agrave; một m&agrave;u x&aacute;m cố định ở mũi gi&agrave;y, ren bảo vệ, cũng như tab g&oacute;t. C&aacute;c tấm da lộn đủ m&agrave;u sắc đan xen nhau xuất hiện ho&agrave; hợp tạo ra một c&aacute;i nh&igrave;n tinh xảo, dấu Swoosh trắng tương phản nổi bật. Ở b&ecirc;n dưới, đế giữa midsole REACT được sử dụng c&ocirc;ng nghệ Air như nguy&ecirc;n bản c&oacute; t&ocirc;ng m&agrave;u hồng trong suốt h&agrave;i ho&agrave; với tổng thể.</p>\r\n\r\n<p><em>C&aacute;c h&igrave;nh ảnh chi tiết của Air Max 270 React &ldquo;Mauve&rdquo;:</em></p>\r\n\r\n<p><img alt=\"Air Max 270 React\" src=\"https://i1.wp.com/snkrvn.com/wp-content/uploads/2020/01/Air-Max-270-React-1.jpg?resize=740%2C520&amp;ssl=1\" /> <img alt=\"Air Max 270 React\" src=\"https://i1.wp.com/snkrvn.com/wp-content/uploads/2020/01/Air-Max-270-React-2.jpg?resize=740%2C520&amp;ssl=1\" /> <img alt=\"Air Max 270 React\" src=\"https://i0.wp.com/snkrvn.com/wp-content/uploads/2020/01/Air-Max-270-React-3.jpg?resize=740%2C740&amp;ssl=1\" /> <img alt=\"Air Max 270 React\" src=\"https://i0.wp.com/snkrvn.com/wp-content/uploads/2020/01/Air-Max-270-React-4.jpg?resize=740%2C519&amp;ssl=1\" /> <img alt=\"Air Max 270 React\" src=\"https://i0.wp.com/snkrvn.com/wp-content/uploads/2020/01/Air-Max-270-React-5.jpg?resize=740%2C519&amp;ssl=1\" /></p>\r\n\r\n<p>Đ&ocirc;i Air Max 270 React &ldquo;Mauve&rdquo; chắc chắn sẽ được đặt l&ecirc;n kệ trong m&ugrave;a xu&acirc;n năm nay tại&nbsp; <a href=\"http://project.com\">project.com</a> tuy nhi&ecirc;n vẫn chưa c&oacute; th&ocirc;ng b&aacute;o ch&iacute;nh thức n&agrave;o về gi&aacute; tiền cũng như thời gian chi tiết. C&oacute; thể thấy BST Xu&acirc;n-H&egrave; của c&aacute;c bạn sneakerheads kh&ocirc;ng n&ecirc;n thiếu phối m&agrave;u xinh xắn n&agrave;y.</p>\r\n', 'Air-Max-270-React.jpg', '2020-05-04', 'Kim Jong-hyun'),
(6, 'Giày có cánh của Nike Air Max 90 xuất hiện phiên bản vàng sang trọng', 'Nike Air Max 90 FlyEase Gold là phiên bản phá cách nhất từ trước đến nay nhằm chào mừng sinh nhật lần thứ 30 của dòng Airmax 90...', '<p>&nbsp;</p>\r\n\r\n<p>Nike Air Max 90 FlyEase l&agrave; phi&ecirc;n bản ph&aacute; c&aacute;ch nhất từ trước đến nay nhằm ch&agrave;o mừng sinh nhật lần thứ 30 của d&ograve;ng Airmax 90.</p>\r\n\r\n<p><br />\r\n<img alt=\"Nike Air Max 90 Gold\" src=\"https://media.thethao247.vn/upload/thanhtung/2020/03/12/the-thao-247-nike-air-max-90-flyease-white-gold-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với đ&ocirc;i c&aacute;nh c&oacute; thể lật l&ecirc;n hoặc hạ xuống theo &yacute; muốn, Nike Air Max 90 FlyEase rất được giới chơi gi&agrave;y chờ đ&oacute;n.</p>\r\n\r\n<p><br />\r\n<img alt=\"Nike Air Max 90 Gold\" src=\"https://media.thethao247.vn/upload/thanhtung/2020/03/12/the-thao-247-nike-air-max-90-flyease-white-gold-3.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Như để tiếp sức th&ecirc;m cho sự hấp dẫn v&agrave; tăng th&ecirc;m lựa chọn cho người d&ugrave;ng, mới đ&acirc;y Nike lại tiếp tục tung ra một phi&ecirc;n bản nữa ngo&agrave;i phi&ecirc;n bản m&agrave;u xanh - hồng - trắng ti&ecirc;u chuẩn.</p>\r\n\r\n<p><br />\r\n<img alt=\"Nike Air Max 90 Gold\" src=\"https://media.thethao247.vn/upload/thanhtung/2020/03/12/the-thao-247-nike-air-max-90-flyease-white-gold-2.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo đ&oacute;, đ&ocirc;i Nike Air Max 90 FlyEase mới cũng c&oacute; th&acirc;n bằng vải lưới v&agrave; da lộn trắng. Nhưng c&aacute;c chi tiết điểm nhấn gồm logo Nike, c&aacute;c miếng nhựa giữ d&acirc;y tr&ecirc;n th&acirc;n v&agrave; viền c&aacute;nh ở đằng sau được nhuộm với m&agrave;u v&agrave;ng Metalic nổi bật.</p>\r\n\r\n<p><br />\r\n<img alt=\"Nike Air Max 90 Gold\" src=\"https://media.thethao247.vn/upload/thanhtung/2020/03/12/the-thao-247-nike-air-max-90-flyease-white-gold-4.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n trong gi&agrave;y l&agrave; miếng l&oacute;t m&agrave;u đen với họa tiệt rắn hổ mang tiết lộ &yacute; nghĩa của đ&ocirc;i gi&agrave;y hướng đến chủ đề về Ai Cập cổ đại giống như những chiếc mũ c&oacute; v&agrave;nh của c&aacute;c vị Pharaoh.</p>\r\n\r\n<p><br />\r\n<img alt=\"Nike Air Max 90 Gold\" src=\"https://media.thethao247.vn/upload/thanhtung/2020/03/12/the-thao-247-nike-air-max-90-flyease-white-gold-5.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo th&ocirc;ng tin từ Nike, Nike Air Max 90 FlyEase đ&atilde; sẵn s&agrave;ng tr&ecirc;n kệ v&agrave; c&oacute; gi&aacute; b&aacute;n từ 149 USD tương đương 3,45 triệu đồng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Nike-Air-Max-90-Gold.jpg', '2020-05-04', 'Kim Jong-hyun'),
(7, 'Nike Air Max 90 FlyEase lật cánh có thêm màu trắng hấp dẫn ', 'Nike Air Max 90 FlyEase từng gây sốt khi xuất hiện với bộ cánh có thể lật lên lật xuống. Bên cạnh hai phiên bản FlyEase xanh trắng và FlyEase vàng kim loại...\r\n\r\n', '<p>&nbsp;</p>\r\n\r\n<p>Nike Air Max 90 FlyEase từng g&acirc;y sốt khi xuất hiện với bộ c&aacute;nh c&oacute; thể lật l&ecirc;n lật xuống. B&ecirc;n cạnh hai phi&ecirc;n bản FlyEase xanh trắng v&agrave; FlyEase v&agrave;ng kim loại ra mắt nh&acirc;n dịp kỷ niệm 30 năm d&ograve;ng Air Max ra đời, mới đ&acirc;y Nike tiếp tục giới thiệu th&ecirc;m một phi&ecirc;n bản nữa được gọi l&agrave; Triple White.</p>\r\n\r\n<p><br />\r\n<img alt=\"Nike Air Max 90\" src=\"https://media.thethao247.vn/upload/thanhtung/2020/04/13/nike-airmax-90-flyease-triple-white-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Phi&ecirc;n bản mới được tạo ra với phần th&acirc;n bằng da trắng nhẹ nh&agrave;ng. Tiếp đến c&aacute;c nh&agrave; thiết kế đ&atilde; kh&eacute;o l&eacute;o tạo điểm nhấn ở cổ v&agrave; mũi gi&agrave;y bằng c&aacute;c chi tiết m&agrave;u xanh băng nhẹ nh&agrave;ng.</p>\r\n\r\n<p><br />\r\n<img alt=\"Nike Air Max 90\" src=\"https://media.thethao247.vn/upload/thanhtung/2020/04/13/nike-airmax-90-flyease-triple-white-2.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong khi đ&oacute;, những dấu hiệu của Nike như logo, biểu tượng đều l&agrave; m&agrave;u trắng v&agrave; gần như t&agrave;ng h&igrave;nh tr&ecirc;n tấm th&acirc;n gi&agrave;y trắng.</p>\r\n\r\n<p><br />\r\n<img alt=\"Nike Air Max 90\" src=\"https://media.thethao247.vn/upload/thanhtung/2020/04/13/nike-airmax-90-flyease-triple-white-3.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cổ gi&agrave;y ph&iacute;a sau với khả năng lật l&ecirc;n xuống cũng được thể hiện với m&agrave;u trắng. Sở dĩ, đ&ocirc;i Nike Air Max 90 FlyEase được gọi l&agrave; Triple White c&oacute; thể l&agrave; do c&oacute; đến 3 dạng m&agrave;u trắng kh&aacute;c nhau xuất hiện.</p>\r\n\r\n<p><br />\r\n<img alt=\"Nike Air Max 90\" src=\"https://media.thethao247.vn/upload/thanhtung/2020/04/13/nike-airmax-90-flyease-triple-white-4.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&oacute; l&agrave; m&agrave;u trắng ng&agrave; của lớp da, m&agrave;u trắng b&oacute;ng của c&aacute;c chi tiết trang tr&iacute; bằng nhựa v&agrave; m&agrave;u trắng b&oacute;ng của phần c&aacute;nh lật.</p>\r\n\r\n<p><br />\r\n<img alt=\"Nike Air Max 90\" src=\"https://media.thethao247.vn/upload/thanhtung/2020/04/13/nike-airmax-90-flyease-triple-white-7.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dự kiến, phi&ecirc;n bản Nike Air Max 90 FlyEase &quot;Triple White&quot; sẽ được b&aacute;n với gi&aacute; từ 120 USD tương đương 2,8 triệu đồng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Nike-Air-Max-90.jpg', '2020-05-04', 'Kim Jong-hyun');

